import React from 'react';

function Users({ users, online }) {
  const status = online ? 'Online' : 'Offline';
  const usersToRender = users.filter(user => user.status === online); // change code here
  const usersItems = <li>{usersToRender}</li>; // change code here
  return (
    <div>
      <h1>Current {status} Users:</h1>
      <ul>
        {usersToRender}
      </ul>
    </div>
  );
};

const users = [
  {
    username: 'Jeff',
    online: true
  },
  {
    username: 'Alan',
    online: false
  },
  {
    username: 'Mary',
    online: true
  },
  {
    username: 'Jim',
    online: false
  },
  {
    username: 'Sara',
    online: true
  },
  {
    username: 'Laura',
    online: true
  }
];

const Task = () => {
  return (
    <div>
      <Users users={users} online={true} />
    </div>
  );
};

export default Task;
