import React, { Component } from 'react';

function sendEmail(name, mail, message) {
  const params = {name, mail, message};
  let alertText = 'Email sending request in process.\nEmail params:\n';
  alertText += Object.keys(params).map(i => `${i}: ${params[i]}`).join('\n');
  alert(alertText);
}

class UncontrolledForm extends Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.nameRef = React.createRef();
    this.mailRef = React.createRef();
    // change code below this line
    this.messageRef = React.createRef();
    this.state = {
      name: '',
      email: '',
      message: ''
    }

    // change code above this line
  }

  handleSubmit = (e) => {
    // change code below this line
    e.preventDefault();
    alert(this.name.current.value);
    //sendEmail(this.nameRef.current.value, this.mailRef, this.messageRef);
    // change code above this line
  };

  render() {
    return (
      <form>
        <div>
          <label for="name">Name:</label>
          <input ref={this.nameRef} value={this.state.value} type="text" id="name" name="user_name" />
        </div>
        <div>
          <label for="mail">E-mail:</label>
          <input  ref={this.mailRef} value={this.state.value} type="email" id="mail" name="user_mail" />
        </div>
        <div>
          <label for="msg">Message:</label>
          <textarea ref={this.messageRef}  id="msg" name="user_message"></textarea>
        </div>
        <button type='submit' onSubmit={this.handleSubmit}>Submit!</button>
      </form>
    );
  }
}

const Task = () => {
  return (
    <div>
      <UncontrolledForm/>
    </div>
  );
};

export default Task;
