import React from 'react';

const friends = [
  {id: 893, name: 'Mikenzi'},
  {id: 871, name: 'Cash'},
  {id: 982, name: 'Steven'},
  {id: 161, name: 'Kimmy'},
  {id: 117, name: 'Doug'}
];

const ListItem = (label, id) => {
  return(
    <li>{label}</li>
  )
}
const List = () => {
  // Render a list using the "friends" being passed in.
  return (
    <ul>
  { friends.map((i,item) => <ListItem label={i} key={id} />)}
    </ul>
  );
};

const Task = () => {
  return (
    <div>
      <List friends={friends} />
    </div>
  );
};

export default Task;
